var path = require('path');
const config = require('config');
var webpack = require('webpack');

module.exports = {
    resolve: {
        extensions: ['.js', '.jsx']
    },
    entry: {
        vendor: ['react', 'lodash', 'react-dom', 'react-router', 'mobx', 'mobx-react']
    },
    output: {
        path: path.resolve(config.path.dllProd),
        filename: '[name].bundle.js',
        library: '[name]_[hash]'
    },
    plugins: [
    	new webpack.DefinePlugin({
            'process.env.NODE_ENV': '"production"'
        }),
        new webpack.DllPlugin({
            path: path.resolve(config.path.dllProd, '[name]-manifest.json'),
            name: '[name]_[hash]'
        })
    ]
};